import React from 'react';
import { Grid, Paper, Typography, Box } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import WarningAmberIcon from '@mui/icons-material/WarningAmber';
import ErrorIcon from '@mui/icons-material/Error';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

const Alerts = () => {
  const alertStatusData = [
    { name: 'Critical', value: 2, color: '#FF5252' },
    { name: 'Warning', value: 3, color: '#FFA726' },
    { name: 'Resolved', value: 5, color: '#4CAF50' }
  ];

  const alerts = [
    { id: 1, device: 'Firewall', type: 'Critical', message: 'High CPU Usage', timestamp: '2 minutes ago' },
    { id: 2, device: 'Switch', type: 'Warning', message: 'High Network Traffic', timestamp: '5 minutes ago' },
    { id: 3, device: 'Server', type: 'Critical', message: 'Low Memory', timestamp: '10 minutes ago' },
    { id: 4, device: 'Endpoint 1', type: 'Warning', message: 'Disk Space Low', timestamp: '15 minutes ago' },
    { id: 5, device: 'Endpoint 2', type: 'Warning', message: 'Service Down', timestamp: '20 minutes ago' }
  ];

  return (
    <Box sx={{ 
      width: '100%', 
      height: 'calc(100vh - 64px)',
      display: 'flex',
      flexDirection: 'column',
      p: 0
    }}>
      <Typography variant="h4" sx={{ mb: 3, px: 2 }}>
        Alert Overview
      </Typography>
      
      {/* Alert Summary Cards */}
      <Paper sx={{ 
        p: 4, // Increased padding
        mb: 3,
        borderRadius: 0,
        width: '100%'
      }}>
        <Grid container spacing={4} alignItems="center"> {/* Increased spacing */}
          <Grid item xs={12} md={4}>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: 3, // Increased gap
              justifyContent: 'center',
              py: 2 // Added vertical padding
            }}>
              <ErrorIcon sx={{ color: '#FF5252', fontSize: 48 }} /> {/* Increased icon size */}
              <Box>
                <Typography variant="h3" color="error">2</Typography> {/* Increased text size */}
                <Typography variant="h6">Critical Alerts</Typography> {/* Increased text size */}
              </Box>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: 3,
              justifyContent: 'center',
              py: 2
            }}>
              <WarningAmberIcon sx={{ color: '#FFA726', fontSize: 48 }} />
              <Box>
                <Typography variant="h3" sx={{ color: '#FFA726' }}>3</Typography>
                <Typography variant="h6">Warning Alerts</Typography>
              </Box>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: 3,
              justifyContent: 'center',
              py: 2
            }}>
              <CheckCircleIcon sx={{ color: '#4CAF50', fontSize: 48 }} />
              <Box>
                <Typography variant="h3" color="success.main">5</Typography>
                <Typography variant="h6">Resolved Alerts</Typography>
              </Box>
            </Box>
          </Grid>
        </Grid>
      </Paper>

      {/* Main Content */}
      <Grid container spacing={0} sx={{ flexGrow: 1, minHeight: 0 }}>
        {/* Chart */}
        <Grid item xs={12} md={5} sx={{ height: '100%' }}>
          <Paper sx={{ 
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            p: 4, // Increased padding
            borderRadius: 0
          }}>
            <Typography variant="h5" gutterBottom>Alert Status Distribution</Typography>
            <Box sx={{ 
              flexGrow: 1, 
              minHeight: 0,
              p: 2 // Added padding
            }}>
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={alertStatusData}
                    cx="50%"
                    cy="50%"
                    innerRadius={0}
                    outerRadius="85%" // Increased chart size
                    dataKey="value"
                    label={({name, percent}) => `${name} ${(percent * 100).toFixed(0)}%`}
                  >
                    {alertStatusData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend 
                    verticalAlign="bottom" 
                    height={48} // Increased legend height
                    formatter={(value) => <span style={{ fontSize: '16px' }}>{value}</span>} // Increased legend text size
                  />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        {/* Recent Alerts */}
        <Grid item xs={12} md={7} sx={{ height: '100%' }}>
          <Paper sx={{ 
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            p: 4, // Increased padding
            borderRadius: 0
          }}>
            <Typography variant="h5" gutterBottom>Recent Alerts</Typography>
            <Box sx={{ 
              flexGrow: 1,
              overflow: 'auto',
              minHeight: 0,
              px: 2 // Added horizontal padding
            }}>
              {alerts.map((alert) => (
                <Box 
                  key={alert.id}
                  sx={{ 
                    p: 3, // Increased padding
                    mb: 3, // Increased margin
                    borderLeft: 8, // Increased border width
                    borderColor: 
                      alert.type === 'Critical' ? '#FF5252' : 
                      alert.type === 'Warning' ? '#FFA726' : '#4CAF50',
                    bgcolor: 'background.paper',
                    borderRadius: 2,
                    boxShadow: 2,
                    '&:hover': {
                      bgcolor: 'action.hover',
                      transform: 'translateX(8px)', // Increased transform
                      transition: 'all 0.3s ease'
                    }
                  }}
                >
                  <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 1 }}> {/* Increased text size */}
                    {alert.device} - {alert.type}
                  </Typography>
                  <Typography variant="body1" sx={{ fontSize: '1.1rem', mb: 1 }}> {/* Increased text size */}
                    {alert.message}
                  </Typography>
                  <Typography variant="subtitle2" color="text.secondary"> {/* Increased text size */}
                    {alert.timestamp}
                  </Typography>
                </Box>
              ))}
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Alerts;
