import React from 'react';
import { Grid, Paper, Typography, Box } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';

const Devices = () => {
  const devices = [
    { name: 'Firewall', type: 'Network Security', status: 'Active', ip: '192.168.1.1' },
    { name: 'Switch', type: 'Network Device', status: 'Active', ip: '192.168.1.2' },
    { name: 'Windows Server', type: 'Server', status: 'Active', ip: '192.168.1.3' },
    { name: 'Endpoint 1', type: 'Workstation', status: 'Active', ip: '192.168.1.4' },
    { name: 'Endpoint 2', type: 'Workstation', status: 'Active', ip: '192.168.1.5' },
  ];

  // Transform devices into pie chart data
  const deviceData = devices.map(device => ({
    name: device.name,
    value: 1,
    color: device.status === 'Active' ? '#4CAF50' : '#FF5252'
  }));

  return (
    <Box sx={{ width: '100%', p: 2 }}>
      <Typography variant="h4" gutterBottom>
        Devices Status
      </Typography>
      
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper 
            sx={{ 
              p: 2,
              height: '400px',
              display: 'flex',
              flexDirection: 'column'
            }}
          >
            <Typography variant="h6" gutterBottom>
              Device Status Overview
            </Typography>
            <Box sx={{ width: '100%', height: '100%', minHeight: 300 }}>
              <ResponsiveContainer>
                <PieChart>
                  <Pie
                    data={deviceData}
                    cx="50%"
                    cy="50%"
                    innerRadius={60}
                    outerRadius={80}
                    paddingAngle={5}
                    dataKey="value"
                  >
                    {deviceData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Device Details
            </Typography>
            {devices.map((device, index) => (
              <Box 
                key={index} 
                sx={{ 
                  mb: 2,
                  p: 1,
                  borderLeft: 6,
                  borderColor: device.status === 'Active' ? '#4CAF50' : '#FF5252',
                  '&:hover': {
                    bgcolor: 'rgba(0, 0, 0, 0.04)'
                  }
                }}
              >
                <Typography variant="subtitle1">
                  {device.name}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  {device.type} - {device.ip}
                </Typography>
                <Typography 
                  variant="body2" 
                  color={device.status === 'Active' ? 'success.main' : 'error.main'}
                >
                  {device.status}
                </Typography>
              </Box>
            ))}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Devices;
