import React from 'react';
import { Grid, Paper, Typography, Box } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';

const Devices = () => {
  const devices = [
    { name: 'Firewall', type: 'Network Security', status: 'Active', ip: '192.168.1.1' },
    { name: 'Switch', type: 'Network Device', status: 'Active', ip: '192.168.1.2' },
    { name: 'Windows Server', type: 'Server', status: 'Active', ip: '192.168.1.3' },
    { name: 'Endpoint 1', type: 'Workstation', status: 'Active', ip: '192.168.1.4' },
    { name: 'Endpoint 2', type: 'Workstation', status: 'Active', ip: '192.168.1.5' },
  ];

  const renderDeviceStatus = (device) => {
    const data = [
      { name: device.status, value: 1 }
    ];

    const color = device.status === 'Active' ? '#4CAF50' : '#FF5252';

    return (
      <Grid item xs={12} sm={6} md={2.4}>
        <Paper 
          sx={{ 
            p: 2,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            height: '100%',
            transition: 'transform 0.2s',
            '&:hover': {
              transform: 'translateY(-4px)',
              boxShadow: 3
            }
          }}
        >
          <Typography variant="h6" gutterBottom align="center" sx={{ fontSize: '1rem' }}>
            {device.name}
          </Typography>
          <Box sx={{ width: '100%', height: 150 }}>
            <ResponsiveContainer>
              <PieChart>
                <Pie
                  data={data}
                  cx="50%"
                  cy="50%"
                  innerRadius={30}
                  outerRadius={40}
                  fill={color}
                  dataKey="value"
                >
                  <Cell fill={color} />
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </Box>
          <Typography variant="body2" color="textSecondary" align="center">
            {device.type}
          </Typography>
          <Typography variant="body2" color="textSecondary" align="center">
            {device.ip}
          </Typography>
        </Paper>
      </Grid>
    );
  };

  return (
    <Box sx={{ width: '100%', p: 2 }}>
      <Typography variant="h4" gutterBottom align="center" sx={{ mb: 4 }}>
        Devices Status
      </Typography>
      <Grid container spacing={3} justifyContent="center">
        {devices.map((device) => renderDeviceStatus(device))}
      </Grid>
    </Box>
  );
};

export default Devices;
