import React from 'react';
import { Grid, Paper, Typography, Box, Stack } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from 'recharts';
import DevicesIcon from '@mui/icons-material/Devices';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';

const Dashboard = () => {
  // Device statistics
  const deviceStats = {
    total: 5,
    active: 5,
    inactive: 0
  };

  const deviceStatusData = [
    { name: 'Active Devices', value: deviceStats.active, color: '#4CAF50' },
    { name: 'Inactive Devices', value: deviceStats.inactive, color: '#FF5252' }
  ];
  const metrics = [
    {
      title: 'Firewall CPU Usage',
      data: [
        { name: 'CPU Usage', value: 65 },
        { name: 'Free CPU', value: 35 }
      ],
      colors: ['#FF6B6B', '#4ECDC4']
    },
    {
      title: 'Switch Port Utilization',
      data: [
        { name: 'Port Usage', value: 75 },
        { name: 'Free Ports', value: 25 }
      ],
      colors: ['#45B7D1', '#98E2F7']
    },
    {
      title: 'Server Memory Usage',
      data: [
        { name: 'Memory Usage', value: 60 },
        { name: 'Free Memory', value: 40 }
      ],
      colors: ['#96C93D', '#DAF7A6']
    }
  ];

  const renderDonutChart = ({ title, data, colors }) => (
    <Box sx={{ 
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: '100%',
      height: '100%'
    }}>
      <Typography variant="h6" gutterBottom align="center" sx={{ mb: 1 }}>
        {title}
      </Typography>
      <Box sx={{ width: '100%', height: 200 }}>
        <ResponsiveContainer>
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              innerRadius={40}
              outerRadius={60}
              paddingAngle={5}
              dataKey="value"
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={colors[index]} />
              ))}
            </Pie>
            <Tooltip />
            <Legend verticalAlign="bottom" height={36} />
          </PieChart>
        </ResponsiveContainer>
      </Box>
    </Box>
  );

  return (
    <Box sx={{ 
      width: '100%', 
      p: 2,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center', // Centers content horizontally
      maxWidth: '1200px', // Limits maximum width
      margin: '0 auto' // Centers the container itself
    }}>
      <Typography variant="h4" gutterBottom sx={{ mb: 4, textAlign: 'center' }}>
        System Overview
      </Typography>

      {/* Device Status Summary */}
      <Paper sx={{ p: 3, mb: 3, width: '100%' }}>
        <Grid container spacing={3} alignItems="center" justifyContent="center">
          <Grid item xs={12} md={4}>
            <Stack direction="row" spacing={2} alignItems="center" justifyContent="center">
              <DevicesIcon sx={{ fontSize: 40, color: 'primary.main' }} />
              <Box>
                <Typography variant="h4" color="primary" align="center">
                  {deviceStats.total}
                </Typography>
                <Typography variant="subtitle1" align="center">Total Devices</Typography>
              </Box>
            </Stack>
          </Grid>
          <Grid item xs={12} md={4}>
            <Stack direction="row" spacing={2} alignItems="center" justifyContent="center">
              <CheckCircleIcon sx={{ fontSize: 40, color: 'success.main' }} />
              <Box>
                <Typography variant="h4" color="success.main" align="center">
                  {deviceStats.active}
                </Typography>
                <Typography variant="subtitle1" align="center">Active Devices</Typography>
              </Box>
            </Stack>
          </Grid>
          <Grid item xs={12} md={4}>
            <Stack direction="row" spacing={2} alignItems="center" justifyContent="center">
              <ErrorIcon sx={{ fontSize: 40, color: 'error.main' }} />
              <Box>
                <Typography variant="h4" color="error.main" align="center">
                  {deviceStats.inactive}
                </Typography>
                <Typography variant="subtitle1" align="center">Inactive Devices</Typography>
              </Box>
            </Stack>
          </Grid>
        </Grid>
      </Paper>

      {/* Metrics Charts */}
      <Box sx={{ 
        display: 'flex', 
        flexDirection: 'row', 
        gap: 3, 
        mb: 3,
        width: '100%',
        justifyContent: 'center',
        overflow: 'auto'
      }}>
        {metrics.map((metric, index) => (
          <Paper 
            key={index}
            sx={{ 
              flex: 1,
              minWidth: '300px',
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
              }
            }}
          >
            {renderDonutChart(metric)}
          </Paper>
        ))}
      </Box>

      <Paper sx={{ p: 3, width: '100%' }}>
        <Grid container spacing={3} justifyContent="center">
          <Grid item xs={12} md={4}>
            <Typography variant="subtitle1" color="primary" sx={{ fontWeight: 'medium', textAlign: 'center' }}>
              Firewall Status: Active
            </Typography>
            <Typography variant="body2" align="center">Active Connections: 1,234</Typography>
          </Grid>
          <Grid item xs={12} md={4}>
            <Typography variant="subtitle1" color="primary" sx={{ fontWeight: 'medium', textAlign: 'center' }}>
              Switch Status: Active
            </Typography>
            <Typography variant="body2" align="center">Error Rate: 0.01%</Typography>
          </Grid>
          <Grid item xs={12} md={4}>
            <Typography variant="subtitle1" color="primary" sx={{ fontWeight: 'medium', textAlign: 'center' }}>
              Server Status: Active
            </Typography>
            <Typography variant="body2" align="center">Uptime: 15 days</Typography>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
};

export default Dashboard;
