import React from 'react';
import { Grid, Paper, Typography, Box } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import { DataGrid } from '@mui/x-data-grid';

const Devices = () => {
  const deviceStatusData = [
    { name: 'Online', value: 8, color: '#4CAF50' },
    { name: 'Offline', value: 2, color: '#FF5252' },
    { name: 'Warning', value: 1, color: '#FFA726' }
  ];

  const columns = [
    { field: 'name', headerName: 'Device Name', width: 150 },
    { field: 'type', headerName: 'Type', width: 150 },
    { field: 'status', headerName: 'Status', width: 120 },
    { field: 'ip', headerName: 'IP Address', width: 150 }
  ];

  const rows = [
    { 
      id: 1,
      name: 'Firewall',
      type: 'Network Security',
      status: 'Active',
      ip: '192.168.1.1'
    },
    { 
      id: 2,
      name: 'Switch',
      type: 'Network Device',
      status: 'Active',
      ip: '192.168.1.2'
    }
  ];

  return (
    <Box sx={{ width: '100%', p: 2 }}>
      <Typography variant="h4" gutterBottom>
        Device Status
      </Typography>
      
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper 
            sx={{ 
              p: 3,
              height: '400px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              backgroundColor: '#fff'
            }}
          >
            <Box sx={{ width: '100%', height: '100%' }}>
              <ResponsiveContainer>
                <PieChart>
                  <Pie
                    data={deviceStatusData}
                    cx="50%"
                    cy="50%"
                    innerRadius={0}
                    outerRadius={100}
                    dataKey="value"
                    label={({name, percent}) => `${name} ${(percent * 100).toFixed(0)}%`}
                  >
                    {deviceStatusData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend 
                    verticalAlign="bottom" 
                    height={36}
                    formatter={(value) => <span style={{ color: '#666', fontSize: '14px' }}>{value}</span>}
                  />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          <Paper sx={{ height: 400, width: '100%' }}>
            <DataGrid
              rows={rows}
              columns={columns}
              pageSize={5}
              rowsPerPageOptions={[5]}
              checkboxSelection={false}
            />
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Devices;
